async def listen_to_uniswap_events():
    async with websockets.connect(websocket_url) as ws:
        # Subscribe to Uniswap V2 factory events
        subscribe_data = {
            "id": 1,
            "jsonrpc": "2.0",
            "method": "eth_subscribe",
            "params": ["logs", {"address": factory_contract_address, "topics": ["0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef"]}  # Keccak256 hash of "PairCreated(address,address,address,uint256)"
        ]}
        await ws.send(json.dumps(subscribe_data))

        while True:
            response = await ws.recv()
            data = json.loads(response)
            
            # Check if it's a PairCreated event
            if 'result' in data and data['result']['topics'][0] == '0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef':
                await handle_event(data['result'])


